import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import "./AddJournal.css";

const AddJournal = ({showModal,setShowModal}) => {
    const [newTitle,setNewTitle]=useState("");
    const [newDesc,setNewDesc]=useState("");
    const navigate=useNavigate();

    const handleSubmit=async()=>{
       const UserInfo=localStorage.getItem("userInfo");
       const ParsedInfo=JSON.parse(UserInfo);
       const res=axios.post("/api/Journal/addjournal",{
        Title:newTitle,
        Description:newDesc,
        Author:ParsedInfo.user.name,
       })
       if((await res).data.result===1){
        setShowModal(false);
        setNewDesc("");
        setNewTitle("");
       }
    }

    const handleClose=async()=>{
        setShowModal(false);
        navigate("/homepage");
    }
  return (
    <div className='Homepage-container'>
        <div className='Homepage-background'></div>
      {showModal && (
  <div className="modal-overlay">
    <div className="modal-content">
      <h2>New Entry</h2>
      <input
        type="text"
        placeholder="Title"
        value={newTitle}
        onChange={(e) => setNewTitle(e.target.value)}
      />
      <textarea
        placeholder="Description"
        rows="20"
        value={newDesc}
        onChange={(e) => setNewDesc(e.target.value)}
      />
      <div className="modal-buttons">
        <button onClick={handleSubmit} variant="outline-dark">Add</button>
        <button onClick={handleClose} variant="outline-dark">Cancel</button>
      </div>
    </div>
  </div>
)}

    </div>
  )
}

export default AddJournal
