import React, { useEffect, useState } from 'react'
import "./Homepage.css"
import Navbar from '../NavBar/NavBar';
import axios from 'axios';
import {FaEdit, FaTrash} from 'react-icons';

const Homepage = ({showModal,setShowModal}) => {
    const [entries,setEntries]=useState([]);
    const [view,setView]=useState();
    const [selectedIndex,setSelectedIndex]=useState('');
    useEffect(()=>{
       const fetchentries=async()=>{
         const UserInfo=localStorage.getItem("userInfo");
         const ParsedInfo=JSON.parse(UserInfo);
         const res=await axios.post("/api/Journal/GetAllJournals",{
            Id:ParsedInfo.user.Id,
            Email:ParsedInfo.user.email,
            Name:ParsedInfo.user.name});
         setEntries(res.data.data);
       }
       fetchentries();
    },[]);

    const handleUpdate=async()=>{
      console.log("update");
    }

  return (
    <div className='Homepage-container'>
        <Navbar view={view} setView={setView} setShowModal={setShowModal}/>
     <div className="Homepage-background"></div>
     <div className='Journal-entries'>
       {entries.length>0?(entries.map((entry,index)=>(
        <div className={`entries-container ${selectedIndex===index?'selected':''}`} key={index} onClick={()=>setSelectedIndex(index)}>
        <div className='heading'>
          <div><h1 className='entry-title'>{entry.title}</h1></div>
          <div style={{display:'flex', flexDirection:'column', justifyContent:'center'}}><h6 style={{fontFamily:'Vivaldi'}}>{entry.createdAt}</h6></div>
          </div>
        
        <div className='entry-description'>{entry.description}</div>
        {selectedIndex===index && (
          <div className='toolkit'>
            <FaEdit onClick={(e)=>{e.stopPropogation(); handleUpdate();}}/>
            </div>
        )}
        </div>
       ))):(<p>"no entries found"</p>)}
      </div>
    </div>
  )
}

export default Homepage
