import React from 'react';
import { Toast, ToastContainer } from 'react-bootstrap';

const AlertBox = ({ message, show, dismisstime = 2000 }) => {
  return (
    <div>
      {/* Internal CSS for brown aesthetic */}
      <style>{`
        .brown-toast {
          background-color: #8B5E3C !important; /* warm brown */
          color: #fff !important;
          border-radius: 10px;
          box-shadow: 0 4px 12px rgba(139, 94, 60, 0.3);
        }

        .brown-toast .toast-body {
          font-family: 'Vivaldi';
          font-size:25px;
          font-weight: 500;
          letter-spacing: 0.4px;
          padding: 12px 16px;
        }
      `}</style>

      <ToastContainer position="top-right" className="p-3" style={{ zIndex: 9999 }}>
        <Toast
          className="brown-toast"
          show={show}
          delay={dismisstime}
          animation={true}
          autohide
        >
          <Toast.Body>{message}</Toast.Body>
        </Toast>
      </ToastContainer>
    </div>
  );
};

export default AlertBox;
