import React from 'react';
import "./Navbar.css";

const Navbar = ({ onAdd, view, setView }) => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light px-4 py-2 shadow-sm">
      <div className="container-fluid d-flex justify-content-between align-items-center">
        
        {/* Left - Add Button */}
        <button 
          onClick={onAdd} 
          className="btn btn-primary"
        >
          + Add
        </button>

        {/* Right - View Options */}
        <div className="btn-group" role="group" aria-label="View toggle">
          <button 
            type="button" 
            className={`btn btn-outline-primary ${view === 'horizontal' ? 'active' : ''}`} 
            onClick={() => setView('horizontal')}
          >
            Horizontal
          </button>
          <button 
            type="button" 
            className={`btn btn-outline-primary ${view === 'vertical' ? 'active' : ''}`} 
            onClick={() => setView('vertical')}
          >
            Vertical
          </button>
        </div>

      </div>
    </nav>
  );
};

export default Navbar;
