import React from 'react';
import "./NavBar.css";
import useNavigate from "react-router-dom";

const Navbar = ({view, setView }) => {
  const navigate=useNavigate();
  const onAdd=async()=>{
    navigate("/addJournal");
  }
  return (
    <nav className="custom-navbar">
      <div className="navbar-container">
        
        {/* Left - Add Button */}
        <button onClick={onAdd} className="add-button"> + Add </button>

        {/* Right - View Options */}
        <div className="view-toggle">
          <button className={`view-button ${view === 'horizontal' ? 'active' : ''}`} onClick={() => setView('horizontal')}> Horizontal </button>
          <button className={`view-button ${view === 'vertical' ? 'active' : ''}`} onClick={() => setView('vertical')}> Vertical </button>
        </div>

      </div>
    </nav>
  );
};

export default Navbar;
