import React, { useState } from 'react'
import {useNavigate } from 'react-router-dom'
import axios from "axios";

const Login = () => {
    const {email,setEmail}=useState('');
    const {password,setPassword}=useState('');
    const navigate=useNavigate();
    const handlesubmit=async(e)=>{
        e.preventDefault();
        const res=await axios.post("/api/Auth/Login",{
            email,
            password
        });
        if(res.result===1){
            navigate("/homepage");
        }else{
            alert("Login Credentials are wrong");
        }
    }
  return (
    <div>
      <form onSubmit={handlesubmit}>
        <h1>Email</h1>
        <input type='text' className='Email' placeholder='Enter Email' onChange={(e)=>setEmail(e.target.value)}></input>
        <h1>Password</h1>
        <input type='text' className='Password' placeholder='Enter Password' onChange={(e)=>setPassword(e.target.value)}></input>
        <button type='submit'>Submit</button>
      </form>
    </div>
  )
}

export default Login
