import React, { useEffect, useState } from "react";
import HTMLFlipBook from "react-pageflip";
import JournalPage from "../JournalPage/JournalPage";
import "./ViewJournal.css";
import axios from "axios";

const ViewJournal = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [author, setAuthor] = useState("");

  useEffect(() => {
    const fetchEntries = async () => {
      const UserInfo = localStorage.getItem("userInfo");
      const ParsedInfo = JSON.parse(UserInfo);
      const res = await axios.post("/api/Journal/GetAllJournals", {
        Id: ParsedInfo.user.Id,
        Email: ParsedInfo.user.email,
        Name: ParsedInfo.user.name,
      });
      setJournalEntries(res.data.data);
      setAuthor(ParsedInfo.user.name);
    };
    fetchEntries();
  }, []);

  // ✅ Construct all pages
  const pages = [
    <div key="cover-front" className="page cover-page">
      <h1 className="journal-name" style={{ textAlign: "center", marginTop: "50%" }}>{author}'s Journal</h1>
    </div>,

    ...journalEntries.flatMap((entry, index) => [
      <div key={`image-${index}`} className="page postcard">
        {entry.image ? (
          <img
            src={entry.image}
            alt="journal"
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              borderRadius: "10px",
            }}
          />
        ) : (
          <p style={{ textAlign: "center", marginTop: "50%" }}>No Image</p>
        )}
      </div>,

      <div key={`text-${index}`} className="page">
        <JournalPage title={entry.title} content={entry.description} />
      </div>
    ]),

    // Optional blank page if needed
    (journalEntries.length * 2 + 2) % 2 !== 0
      ? <div key="blank" className="page" />
      : null,

    <div key="cover-back" className="page cover-page">
      <h2 style={{ textAlign: "center", marginTop: "50%" }}> </h2>
    </div>
  ].filter(Boolean); // ✅ Remove `null` or `false`

  return (
    <div className="book-container">
      <div className="page-background"></div>
      {journalEntries.length > 0 && (
        <HTMLFlipBook
          width={400}
          height={600}
          className="flipbook"
          startPage={0}
          showCover={true}
        >
          {pages}
        </HTMLFlipBook>
      )}
    </div>
  );
};

export default ViewJournal;
