import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate, useLocation } from 'react-router-dom';

const UpdateJournal = ({showModal,setShowModal}) => {
    const [newTitle,setNewTitle]=useState("");
    const [newDesc,setNewDesc]=useState("");
    const navigate=useNavigate();
    const location=useLocation();
    const entry=location.state?.entry;
    const UserInfo=JSON.parse(localStorage.getItem("userInfo"));

    useEffect(()=>{
        setNewTitle(entry.title);
        setNewDesc(entry.description);
    },[]);

    const handleSubmit=async ()=>{
        const res=await axios.put("/api/Journal/updateJournal",{
            Id:entry.id,
            Title:newTitle,
            Description:newDesc,
            Author:UserInfo.user.name,
        })
        if(res.data.Result===1){
            setShowModal(false);
            setNewTitle("");
            setNewDesc("");
            navigate("/homePage");
        }
    }

    const handleClose=()=>{
        setShowModal(false);
        navigate("/homePage");
    }
  return (
    <div className='Homepage-container'>
        <div className='Homepage-background'></div>
      {showModal && (
  <div className="modal-overlay">
    <div className="modal-content">
      <h2 style={{color:'black'}}>Update Entry</h2>
      <input
        type="text"
        placeholder="Title"
        value={newTitle}
        onChange={(e) => setNewTitle(e.target.value)}
        className='newtitle'
      />
      <textarea
        placeholder="Description"
        rows="20"
        value={newDesc}
        onChange={(e) => setNewDesc(e.target.value)}
      />
      <div className="modal-buttons">
        <button onClick={handleSubmit} className='buttons'>Update</button>
        <button onClick={handleClose} className="buttons">Cancel</button>
      </div>
    </div>
  </div>
)}

    </div>
  )
}

export default UpdateJournal
